    def joue_note(self,val_note,time):
        if val_note == "silence":
            sleep((60/self.tempo)*time)
        else:
            self.buz.set_freq(self.freq_notes[val_note])
            self.buz.start()
            sleep((60/self.tempo)*time)
            self.buz.stop()      

    def joue_melodie(self,melodie):
        try:
            print(f"Melodie: {melodie['titre']}")
            self.tempo = melodie["tempo"]
            for note,duree in melodie["notes"]:
                self.joue_note(note,duree)
                sleep(0.01)
        finally:
            self.buz.stop()


